function getRestaurants(n,o){var e="https://shitfoodapi.azurewebsites.net/api/getshit?lat="+n+"&lng="+o;axios.get(e).then(function(n){for(var o=0;o<n.data.length;o++)L.marker([n.data[o].lat,n.data[o].lng],{icon:cutleryIconColour(n.data[o].foodHygieneRating),alt:n.data[o].name,title:n.data[o].name}).addTo(mymap).bindPopup("<strong>"+n.data[o].name+"</strong><br>Food Hygiene Rating: <strong>"+n.data[o].foodHygieneRating+"</strong> ("+hygieneRatingPhrase(n.data[o].foodHygieneRating)+")");console.log(n.data)}).catch(function(n){console.log(n)})}function cutleryIconColour(n){return"2"===n?L.icon({iconUrl:"./img/markers/cutlery-yellow.svg",iconSize:[36,36],iconAnchor:[10,10],popupAnchor:[34,0]}):"1"===n?L.icon({iconUrl:"./img/markers/cutlery-orange.svg",iconSize:[36,36],iconAnchor:[10,10],popupAnchor:[34,0]}):"0"===n?L.icon({iconUrl:"./img/markers/cutlery-red.svg",iconSize:[36,36],iconAnchor:[10,10],popupAnchor:[34,0]}):L.icon({iconUrl:"./img/markers/cutlery.svg",iconSize:[36,36],iconAnchor:[10,10],popupAnchor:[34,0]})}function hygieneRatingPhrase(n){switch(n){case 2:return"Improvement Necessary";case 1:return"Major Improvement Necessary";case 0:return"Urgent Improvement Necessary"}}